import test from "ava";
import { isErc20Transfer } from "../../src/erc20";

test("isErc20Transfer", (t) => {
  const emptyInput = "0x";
  const illegalInput = "123";
  const uniswapInput =
    "";
  const erc20TransferInput =
    "0xa9059cbb000000000000000000000000477b8d5ef7c2c42db84deb555419cd817c336b6f000000000000000000000000000000000000000000000000000000001ad27480";

  t.false(isErc20Transfer(emptyInput));
  t.false(isErc20Transfer(illegalInput));
  t.false(isErc20Transfer(uniswapInput));
  t.true(isErc20Transfer(erc20TransferInput));
});
